c..   we want  x^n * expand( 1/x^n *( (1-x^n) * dcoefs/(1-x) + sgg1) )
c..   where n=ncat2

c..   these are the coefficients of (1-x)^i in expand( (1-x^n)/(1-x)*dcoefs ):
      helpd=0.d0
      do i=0,ncat2-1
         helpd(i) = -bininv(-ncat2,i+1)*dcoefs2(nmtlim(2,1),1.d0,
     &        dlxm1,dlxm1**2,dlxm1**3)
      enddo

C$$$      modified = 1
c..   this is  G(x) = sigma/xt^ncat2 :
      do k=0,nsoft(2,1)
         sum=0.d0
         do i=0,k
            sum = sum + bininv(ncat2,i) * ( help(k-i) + helpd(k-i) )
         enddo
         helpx(k) = sum
      enddo

      sgg2exp = 0.d0
      do i=0,nsoft(2,1)
         sgg2exp = sgg2exp + xm1**i * ( helpx(i) )
      enddo

      sgg2exp = xt**ncat2 * sgg2exp

      if (nmtlim(2,1).gt.0) sgg2exp = sgg2exp + sgg2mtexp(xt)

c..   factor out SM Wilson coefficient:
      sgg2exp = sgg2exp - 2*c1sm1/c1sm0*sgg1exp(nmtlim(2,1),nsoft(2,1)
     &     ,ncat2,xt)

      return
      end

